const dateTool = {
    parse (dateStr) {
        var formatStr = 'YYYY-MM-DDTHH:mm:ssZ'
        if (dateStr.indexOf('.') >= 0) {
            formatStr = 'YYYY-MM-DDTHH:mm:ss.SSSSSSSSSZ'
        }
        return moment(dateStr, formatStr)
    },
    format (moment) {
