    },
    formatYMDHMS (dateStr) {
        let time = dateTool.parse(dateStr)
        return moment(time).format('YYYY-MM-DD HH:mm:ss')
    },
    getReplyTime (date) {
        let time = dateTool.parse(date).valueOf()
        let currentT = new Date().getTime()
        let diff = (currentT - time) / 1000
        if (diff < 60) {
